import React, {Component} from 'react';
import DatePicker from 'react-datepicker';//import date picker
import "react-datepicker/dist/react-datepicker.css";//import styling for date picker
import axios from 'axios';

export default class CreateExercise extends Component{
    constructor(props){
        super(props);
        console.log('check 123');
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state={ //state is basically how you create variables in react, you are never gonna do something like let in js
            username: '',
            description: '',
            duration: 0,
            date: new Date(),
            users: []//users associated with an exercise
        };//whenever you update the state, react will automatically update the page for you
    }


    //hardcoding single user
    //a react life-cycle method: a method react will automatically call
    componentDidMount() {//automatically called right before anything displays on the page
        this.setState({
            users: ['test user'],
            username: 'test user'
        });
    }    
    onChangeUsername(e){
        this.setState({
            username: e.target.value
        });
    }
    onChangeDescription(e){
        this.setState({
            description: e.target.value
        });
    }
    onChangeDuration(e){
        this.setState({
            duration: e.target.value
        });
    }
    onChangeDate(date){
        this.setState({
            date: date
        });
    }

    onSubmit(e) {
        console.log('hiiiiiiiiiiiiiiiiiii');
        e.preventDefault(); //prevent default HTML form behaviour from taking place
        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date
        }
        console.log(exercise);
        axios.post('http://localhost:5000/exercises/add',exercise)
        .then(res => console.log(res.data));

        window.location ='/';//redirect to home page

    }
    render() {
        console.log('hi123');
        return (
            <div>
                <h3>Create New Exercise Log</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <select ref="userInput"
                        required
                        className="form-control"
                        value={this.state.username}
                        onChange={this.onChangeUsername}>
                        {
                            this.state.users.map(function(user){
                                return <option
                                key={user}
                                value={user}>{user}
                                </option>;
                            })
                        }

                        </select>

                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            value={this.state.description}
                            onChange={this.onChangeDescription}>
                        </input>
                    </div>
                    <div className="form-group">
                        <label>Duration (in minutes): </label>
                        <input
                            type="text"
                            className="form-control"
                            value={this.state.duration}
                            onChange={this.onChangeDuration}>
                        </input>
                    </div>
                    <div className="form-group">
                        <label>Date: </label>
                        <div>
                            <DatePicker 
                                selected={this.state.date}
                                onChange={this.onChangeDate}
                            /> 
                        </div>
                    </div>
                    <div className="form-group">
                        <input
                            type="submit" value="Create Exercise" className="btn btn-primary"    
                        >

                        </input>
                    </div>
                </form>
            </div>
        );
    }
}
